mapper.update:
UPDATE t_sale_list
SET state = 1
WHERE sale_list_id = #{saleListId}

mapper.select:
SELECT t1.* ,t2.sale_number ,t2.state,t2.sale_date,t3.customer_name,t4.goods_type_name FROM t_sale_list_goods t1 LEFT JOIN t_sale_list t2 on t2.sale_list_id = t1.sale_list_id LEFT JOIN t_customer t3 ON t3.customer_id = t2.customer_id LEFT JOIN t_goods_type t4 ON t4.goods_type_id = t1.goods_type_id
sale_date between #{sTime} and #{eTime}
and t4.goods_type_id = #{goodsTypeId}
and (goods_code like concat('%',#{codeOrName},'%') OR goods_name LIKE concat('%',#{codeOrName},'%'))
